import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import Ball from './Ball';
import Score from './Score';
import FieldImg from '../img/Field.png';
import Character from './Character.js';
import GameManager from './GameManager';
import GameModeManager from './GameModeManager';

// const PLAYERMODE = 2;
const GAME_WIDTH = 991;
const GAME_HEIGHT = 678;

const GameBox = (props) => {
    const playerRef = useRef(null);
    const opponentRef = useRef(null);
    const scoreLeft = useRef(null);
    const scoreRight = useRef(null);
    const managerRef = useRef(null);


    useEffect(() => {
        if (managerRef.current && managerRef.current.innerText === "quitting") {
            console.log("QUITTING");
        }
    }, [manager.ref.cu]);

    return (
        (managerRef.current && managerRef.current.innerText === "quiting") ? (
            <GameModeManager height={GAME_HEIGHT} width={GAME_WIDTH}/>
        ) :
        (
            <GameBoxDiv height={props.height} width={props.width}>
            <Score ref={scoreLeft} left={300}></Score>
            <Score ref={scoreRight} left={600}></Score>
            <Character id="Player" ref={playerRef} controlMode={props.leftPlayer} x={100} y={200} 
            socket={props.socket}/>
            <Character id="Opponent" ref={opponentRef} controlMode={props.rightPlayer}
             x={props.opponentPosX} y={props.opponentPosY}
             socket={props.socket}/>
            <Ball height={props.height} width={props.width} managerRef={managerRef} playerRef={playerRef} opponentRef={opponentRef}
            scoreLeft={scoreLeft} scoreRight={scoreRight} socket={props.socket} x={props.ballX} y={props.ballY}/>
            <GameManager ref={managerRef} scoreLeft={scoreLeft} scoreRight={scoreRight}
            height={props.height} width={props.width} socket={props.socket}
            ></GameManager>
            </GameBoxDiv>
        )
    );
};


const GameBoxDiv = styled.div`
  height: ${(props) => props.height}px;
  width: ${(props) => props.width}px;
  //top : 100px;
  position: relative;
  background-image: url(${FieldImg});
`;

export default GameBox;
