import { useState } from 'react';
import GameBox from './GameBox';
import styled from 'styled-components'
import io from 'socket.io-client'

const socket = io('ws://localhost:8000');

socket.on('connect', () => {
    console.log('Connected to socket.io server');
});

socket.on('connect_error', (error) => {
    console.error('Failed to connect to socket.io server', error);
});

const MultiplayerManager = (props) => {
    const [infos, setInfos] = useState({
        opponentX: 860,
        opponentY: 200,
    })

    useEffect(effect)
    useEffect(() => {
        // Listen to the 'connect' event
        socket.on('connect', () => {
           console.log('Connected to server');
        });

        // Listen to the 'opponentMoved' event
        socket.on('opponentMoved', (data) => {
           setInfos({
              opponentX: data.x,
              opponentY: data.y,
           });
        });

        // Emit a 'playerMoved' event
        const handleMove = (x, y) => {
           socket.emit('playerMoved', { x, y });
        }

        // Clean up the event listeners when the component unmounts
        return () => {
           socket.off('connect');
           socket.off('opponentMoved');
        };
     }, [setInfos]);

    return (
        <GameBox height={props.height} width={props.width}
        opponentPosX={infos.opponentX} opponentPosY={infos.opponentY} socket={socket}>
        </GameBox>
    )
}



export default MultiplayerManager;
